/**
 * @author [Alex Neo]
 * @email [alex.neo0115@gmail.com]
 * @create date 2022-01-12 18:08:03
 * @modify date 2022-01-12 18:08:03
 * @desc [create sider view]
*/

import React, { useState, useEffect }  from "react";
import { useSelector, useDispatch } from "react-redux";
import { Layout, Menu, Typography } from "antd";
import "../components/css/SliderMenu.css";
import {
    PlusOutlined
} from "@ant-design/icons";
import { addMenuItem, increaseCounter } from "redux/action";
import CounterView from "./CounterView";

const { Content, Sider } = Layout;
const { Title } = Typography;



const SliderView = (pageTitle) => {
    //get state from store by useSelector
    const todoMenu = useSelector(state => 
        state.addMenuItemReducer.todoMenu
    );

    //useDispatch to generate dispatch func
    const dispatch = useDispatch();
    const addMenus = (payload) => {
        return () => {
            const actionAddMenuItem = addMenuItem(payload);
            dispatch(actionAddMenuItem);
        }
    }


    const todoCounter= useSelector(state => 
        console.log(state)
    );


    const clickIncrease = (pos, value) => {
        console.log("click add", pos, value)
        const actionIncrease = increaseCounter(pos, value);
        dispatch(actionIncrease);
    }

    const clickDecrease = (pos, value) => {
        console.log("click dec", pos, value)
    }


   return( 
    <Layout style={{ minHeight: "100vh", padding: 0  }}>
         <Sider>
            <Menu theme="dark" mode="inline">
                {todoMenu.map((todo, i) => 
                    <Menu.Item key={i}>
                        <CounterView 
                            value={i} 
                            onIncreaseClick={() => {
                                clickIncrease(i, 1);
                            }} 
                            onDecreaseClick={() => {
                                clickDecrease(i, 1);
                            }}/>
                    </Menu.Item>
                )}
                <Menu.Divider />
                {<div style={{color:"#FFF", width:"200px", height:"30px"}}> Counter{todoCounter}</div>}
                <Menu.Divider />
                <Menu.Item icon={<PlusOutlined />} onClick={addMenus(1)}>
                    Add counter
                </Menu.Item>
            </Menu>
        </Sider>
        <Layout className="site-layout">
          <Content style={{ margin: "0 16px" }}>
            <div>
              <Title level={1}>{pageTitle.pageTitle}</Title>
            </div>
          </Content>
        </Layout>

    </Layout>
   );

};


export default SliderView;
