import React, { useState, useEffect }  from 'react';

import { Layout, Menu, Typography } from 'antd';
import "../components/css/SliderMenu.css";
import {
    PlusOutlined
} from '@ant-design/icons';

const { Header, Content, Footer, Sider } = Layout;
const { Title } = Typography;



const SliderView = (pageTitle) => {

    const [myMenu, setMyMenu] = useState(0);

    const handleMenuClick = () => {
        setMyMenu(myMenu+1);
    }


   return( 
    <Layout style={{ minHeight: '100vh', padding: 0  }}>
         <Sider>
            <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">

                {myMenu === 0 ? (
                    <Menu.Item key="1" >
                        Option 1
                    </Menu.Item>
                ) : (
                    <View style={{ marginTop: -Metrics.sizeDp[5] }}>
                        {remainAmount === 0.0 ? (
                            <Text
                                style={[styles.date, { ...GlobalFonts.body2 }]}
                            >{`${Trans.payment_received} ${date}`}</Text>
                        ) : (
                            <Text
                                style={[styles.date, { ...GlobalFonts.body2 }]}
                            >{`${Trans.due} ${dueDate}`}</Text>
                        )}
                        <Text style={styles.codeAndDate}>{`${code} • ${date}`}</Text>
                    </View>
                )}
                <Menu.Divider />
                <Menu.Item icon={<PlusOutlined />} onClick={handleMenuClick}>
                    Add counter
                </Menu.Item>
            </Menu>
        </Sider>
        <Layout className="site-layout">
          <Content style={{ margin: '0 16px' }}>
            <div>
              <Title level={1}>{pageTitle.pageTitle}</Title>
            </div>
          </Content>
        </Layout>

    </Layout>
   );

};


export default SliderView;
