/**
 * @author [Alex Neo]
 * @email [alex.neo0115@gmail.com]
 * @create date 2022-01-12 18:08:03
 * @modify date 2022-01-12 18:08:03
 * @desc [create reducer]
*/

import { actionTypes } from "../action/index"


const initState = {
    todoMenu: [],
    counterList: []
}


const reducer = (state = initState, action) => {
    const tempTodo = state.todoMenu.map(list => list);
    const tempCount = state.counterList.map(list => list);


    switch (action.type){
        case actionTypes.ADD_MENU_ITEMS:
            tempTodo.push(action.payload);
            tempCount.push({data: 0});
            return {
                todoMenu: tempTodo,
                counterList: tempCount
            };
        case actionTypes.INCREASE_COUNTER:
            const tempIncCount = state.counterList.map(list => list);
            //get current index
            const i = tempIncCount.findIndex(el => el.id === action.payload);
            //update array if exist else add
            if (i > -1) {
                const j = tempIncCount[i].data + action.payload.data
                tempIncCount[i] = {...tempIncCount[i], data : j} 
            }else{
                tempIncCount.push(action.payload);
            } 
            return {
                todoMenu: tempTodo,
                counterList: tempIncCount,
            };
        case actionTypes.DECREASE_COUNTER:
            const tempDecCount = state.counterList.map(list => list);
            //get current index
            const x = tempDecCount.findIndex(el => el.id === action.payload.id);
            //update array if exist else add
            if (x > -1) {
                const y = tempDecCount[x].data - action.payload.data
                tempDecCount[x] = {...tempDecCount[x], data : y < 0? 0: y} 
            }else{
                tempDecCount.push(action.payload);
            } 
            return {
                todoMenu: tempTodo,
                counterList: tempDecCount,
            };
        default:
            return state;
    }
}


export default reducer;
