import React, { useState, useEffect }  from 'react';

import { Layout, Menu, Typography } from 'antd';
import "../components/css/SliderMenu.css";
import {
    PlusOutlined
} from '@ant-design/icons';

const { Content, Sider } = Layout;
const { Title } = Typography;



const SliderView = (pageTitle) => {

    const [myMenu, setMyMenu] = useState([0]);

    const handleMenuClick = () => {
        console.log("start: "+ myMenu);
        setMyMenu( arr => [...myMenu, `${myMenu.length}`]);
        console.log("end: "+ myMenu);

    }


   return( 
    <Layout style={{ minHeight: '100vh', padding: 0  }}>
         <Sider>
            <Menu theme="dark" mode="inline">
                {myMenu.length === 0 ? (
                    <Menu.Item key="1" >
                        Option 1
                    </Menu.Item>
                ) : (
                    myMenu.map( (item, i) => 
                    <Menu.Item key={i} >
                        Option {i}
                    </Menu.Item>
                    )

                )}
                <Menu.Divider />
                <Menu.Item icon={<PlusOutlined />} onClick={handleMenuClick}>
                    Add counter
                </Menu.Item>
            </Menu>
        </Sider>
        <Layout className="site-layout">
          <Content style={{ margin: '0 16px' }}>
            <div>
              <Title level={1}>{pageTitle.pageTitle}</Title>
            </div>
          </Content>
        </Layout>

    </Layout>
   );

};


export default SliderView;
