import { combineReducers } from "redux";
let initMenuItem = 0;

const addMenuItemReducer = (state = initMenuItem, action) => {
 switch (action.type){
     case "ADD_MENU_ITEMS":
         return state = state + action.payload;
    default:
        return state;
 }
}

const reducer = combineReducers({
    addMenuItemReducer
})

export default reducer;
