import { combineReducers } from "redux";
const initialState = {
	menuItems: [0],
};

const addMenuItemReducer = (state = initialState, action) => {
    console.log("reducer function: ", state, action)
    switch (action.type){
        case "ADD_MENU_ITEMS":
            return initialState.menuItems.push(action.payload);
        default:
            return state;
    }
}

const reducer = combineReducers({
    addMenuItemReducer
})

export default reducer;
