import { combineReducers } from "redux";
let initMenuItem = [0];

const addMenuItemReducer = (state = initMenuItem, action) => {
    console.log("reducer function: ", state, action)
    switch (action.type){
        case "ADD_MENU_ITEMS":
            initMenuItem.push(action.payload);
            return initMenuItem;
        default:
            return state;
    }
}

const reducer = combineReducers({
    addMenuItemReducer
})

export default reducer;
