/**
 * @author [Alex Neo]
 * @email [alex.neo0115@gmail.com]
 * @create date 2022-01-12 18:08:03
 * @modify date 2022-01-12 18:08:03
 * @desc [create action]
*/

const ADD_MENU_ITEMS = "ADD_MENU_ITEMS";
const INCREASE_COUNTER = "INCR_COUNTER";
const DECREASE_COUNTER = "DEC_COUNTER";

export const actionTypes = {
    ADD_MENU_ITEMS,
    INCREASE_COUNTER,
    DECREASE_COUNTER
};

const addMenuItem = (payload) => {
    return {
        type: "ADD_MENU_ITEMS",
        payload: payload
    }
}

const increaseCounter = (index, data) => {
    return {
        type: "INCR_COUNTER",
        payload: { index, data }
    }
}

const decreaseCounter = (data) => {
    return {
        type: "DEC_COUNTER",
        payload: { data }
    }
}

export{
    addMenuItem,
    increaseCounter,
    decreaseCounter
}
