import { combineReducers } from "redux";

const ADD_MENU_ITEMS = "ADD_MENU_ITEMS";
const INCREASE_COUNTER = "INCR_COUNTER";
const DECREASE_COUNTER = "DEC_COUNTER";


const initState = {
    todoMenu: [],
    counterList: []
}


const addMenuItemReducer = (state = initState, action) => {
    switch (action.type){
        case ADD_MENU_ITEMS:
            const tempTodo = state.todoMenu.map(list => list);
            tempTodo.push(action.payload);
            return {
                todoMenu: tempTodo,
            };
        default:
            return state;
    }
}

const counterReducer = (state = initState, action) => {
    switch (action.type){
        case INCREASE_COUNTER:
            const tempCount = state.counterList.map(list => list);
            console.log("increase 2", tempCount)
            const i = tempCount.findIndex(el => el.id === action.payload.id);
            if (i > -1) {
                const j = tempCount[i].data + action.payload.data
                console.log("increase 2-2", j)
            }else{
                tempCount.push(action.payload);
            } 
            console.log("increase 3", tempCount)
            return {
                counterList: tempCount,
            };;
        default:
            return state;
    }
}

const reducer = combineReducers({
    addMenuItemReducer,
    counterReducer
})

export default reducer;
