/**
 * @author [Alex Neo]
 * @email [alex.neo0115@gmail.com]
 * @create date 2022-01-12 18:08:03
 * @modify date 2022-01-12 18:08:03
 * @desc [create sider view]
*/

import { combineReducers } from "redux";

const ADD_MENU_ITEMS = "ADD_MENU_ITEMS";
const INCREASE_COUNTER = "INCR_COUNTER";
const DECREASE_COUNTER = "DEC_COUNTER";


const initState = {
    todoMenu: [],
    counterList: []
}


const addMenuItemReducer = (state = initState, action) => {
    switch (action.type){
        case ADD_MENU_ITEMS:
            const tempTodo = state.todoMenu.map(list => list);
            tempTodo.push(action.payload);
            return {
                todoMenu: tempTodo,
            };
        default:
            return state;
    }
}

const counterReducer = (state = initState, action) => {
    console.log("counter ", state.counterList, action)
    switch (action.type){
        case INCREASE_COUNTER:
            console.log("increase 1", action.payload)
            const tempCount = state.counterList.map(list => list);
            console.log("increase 2", tempCount)
            const i = tempCount.findIndex(el => el.id === action.payload.id);
            if (i > -1) {
                const j = tempCount[i].data+1
                console.log("increase 2-2", j, action.payload.data)
                tempCount[i] = action.payload; 
            }else{
                tempCount.push(action.payload);
            } 
            console.log("increase 3", tempCount)
            return {
                counterList: tempCount,
            };;
        default:
            return state;
    }
}

const reducer = combineReducers({
    addMenuItemReducer,
    counterReducer
})

export default reducer;
