 * @desc [create reducer]
*/

import { combineReducers } from "redux";

const ADD_MENU_ITEMS = "ADD_MENU_ITEMS";
const INCREASE_COUNTER = "INCR_COUNTER";
const DECREASE_COUNTER = "DEC_COUNTER";


const initState = {
    todoMenu: [],
    counterList: []
}


const addMenuItemReducer = (state = initState, action) => {
    switch (action.type){
        case ADD_MENU_ITEMS:
            const tempTodo = state.todoMenu.map(list => list);
            tempTodo.push(action.payload);
            const tempCount = state.counterList
            tempCount.push(...tempCount, {data:0})
            return {
                todoMenu: tempTodo,
            };
        default:
            return state;
    }
}

const counterReducer = (state = initState, action) => {
    switch (action.type){
        case INCREASE_COUNTER:
            const tempIncCount = state.counterList.map(list => list);
            //get current index
            const i = tempIncCount.findIndex(el => el.id === action.payload.id);
            //update array if exist else add
            if (i > -1) {
                const j = tempIncCount[i].data + action.payload.data
                tempIncCount[i] = {...tempIncCount[i], data : j} 
            }else{
                tempIncCount.push(action.payload);
            } 
            return {
                counterList: tempIncCount,
            };
        case DECREASE_COUNTER:
                //copy array
                const tempDecCount = state.counterList.map(list => list);
                //get current index
                const x = tempDecCount.findIndex(el => el.id === action.payload.id);
                //update array if exist else add
                if (x > -1) {
                    const y = tempDecCount[x].data - action.payload.data
                    tempDecCount[x] = {...tempDecCount[x], data : y < 0? 0: y} 
                }else{
                    tempDecCount.push(action.payload);
                } 
                return {
                    counterList: tempDecCount,
                };
        default:
            return state;
    }
}

const reducer = combineReducers({
    addMenuItemReducer,
    counterReducer
})

export default reducer;
