import React, { useState, useEffect }  from 'react';
import { useSelector } from 'react-redux';
import { Layout, Menu, Typography } from 'antd';
import "../components/css/SliderMenu.css";
import {
    PlusOutlined
} from '@ant-design/icons';
import store from 'redux/store';
import { addMenuItem } from 'redux/action';

const { Content, Sider } = Layout;
const { Title } = Typography;



const SliderView = (pageTitle) => {

    let myMenu = store.getState().addMenuItemReducer;
    const todoMenu = useSelector(state => 
        state.initMenuItem
    );
    console.log("redux todoMenu", todoMenu)

    // const handleMenuClick = () => {
    //     console.log("start: "+ myMenu);
    //     setMyMenu( arr => [...myMenu, `${myMenu.length}`]);
    //     console.log("end: "+ myMenu);
    // }
    useEffect(() => {
        console.log("local val", myMenu.length);
        console.log("redux val", store.getState().addMenuItemReducer);

    }, [store.getState]);

    const addMenus = (payload) => {
        return () => {
            const actionAddMenuItem = addMenuItem(payload);
            store.dispatch(actionAddMenuItem);
            myMenu = store.getState().addMenuItemReducer;
            console.log("Deal", myMenu.length);

        }
    }


   return( 
    <Layout style={{ minHeight: '100vh', padding: 0  }}>
         <Sider>
            <Menu theme="dark" mode="inline">
                {myMenu.length === 0 ? (
                    <Menu.Item key="1" >
                        Option 1
                    </Menu.Item>
                ) : (
                    myMenu.map( (item, i) => 
                    <Menu.Item key={i} >
                        Option {i}
                    </Menu.Item>
                    )

                )}
                <Menu.Divider />
                <Menu.Item icon={<PlusOutlined />} onClick={addMenus(1)}>
                    Add counter
                </Menu.Item>
            </Menu>
        </Sider>
        <Layout className="site-layout">
          <Content style={{ margin: '0 16px' }}>
            <div>
              <Title level={1}>{pageTitle.pageTitle}</Title>
            </div>
          </Content>
        </Layout>

    </Layout>
   );

};


export default SliderView;
